using System;using System.Linq;class Program{    static void Main()    {        while (true)        {            Console.WriteLine("Выберите задание:");            Console.WriteLine("1 - Проверка чисел на принадлежность интервалу");            Console.WriteLine("2 - Вычисление периметра и площади треугольника");            Console.WriteLine("3 - Поиск минимума и максимума в массиве");            Console.WriteLine("0 - Выход");                        string choice = Console.ReadLine();            if (choice == "1")            {                CheckNumbersInRange();            }            else if (choice == "2")            {                TriangleCalculations();            }            else if (choice == "3")            {                FindMinMaxInArray();            }            else if (choice == "0")            {                Console.WriteLine("Выход из программы.");                break;            }            else            {                Console.WriteLine("Некорректный ввод. Пожалуйста, выберите 1, 2, 3, 4 или 0.");            }        }    }        static void CheckNumbersInRange()    {        Console.WriteLine("Введите три целых числа:");        int a = int.Parse(Console.ReadLine());        int b = int.Parse(Console.ReadLine());        int c = int.Parse(Console.ReadLine());        int intervalStart = 1;        int intervalEnd = 15;        Console.WriteLine($"Числа, которые принадлежат интервалу [{intervalStart}, {intervalEnd}]:");        if (a >= intervalStart && a <= intervalEnd) Console.WriteLine(a);        if (b >= intervalStart && b <= intervalEnd) Console.WriteLine(b);        if (c >= intervalStart && c <= intervalEnd) Console.WriteLine(c);    }        static void TriangleCalculations()    {        Console.WriteLine("Введите три стороны треугольника:");        double a = double.Parse(Console.ReadLine());        double b = double.Parse(Console.ReadLine());        double c = double.Parse(Console.ReadLine());        if (a + b > c && a + c > b && b + c > a)        {            double perimeter = a + b + c;            Console.WriteLine($"Периметр треугольника: {perimeter}");            double p = perimeter / 2;            double area = Math.Sqrt(p * (p - a) * (p - b) * (p - c));            Console.WriteLine($"Площадь треугольника: {area}");            if (a == b && b == c)                Console.WriteLine("Треугольник равносторонний.");            else if (a == b || a == c || b == c)                Console.WriteLine("Треугольник равнобедренный.");            else                Console.WriteLine("Треугольник разносторонний.");        }        else        {            Console.WriteLine("Треугольник с такими сторонами не существует.");        }    }    static void FindMinMaxInArray()    {        int length = 11;        int[] X = new int[length];        Console.WriteLine("Массив X:");        for (int i = 0; i < length; i++)        {            Console.Write($"Введите элемент {i + 1}: ");            X[i] = int.Parse(Console.ReadLine());        }        int min = X[0];        int max = X[0];        foreach (int number in X)        {            if (number < min) min = number;            if (number > max) max = number;        }        Console.WriteLine("\nВведенный массив:");        foreach (int number in X)        {            Console.Write(number + " ");        }        Console.WriteLine($"\nМинимальное значение: {min}");        Console.WriteLine($"Максимальное значение: {max}");    }}